                                   data: {job_cat: job_cat, job_psn: job_psn},                                                success: function (data) {                                                    $('.skill_set').html(data);                                                }                                            });                                        });                                        $('#interview_result').change(function () {                                            var selected_val = $(this).val();                                            if (selected_val == "Selected") {                                                $('.rejected_result').hide();                                                $('.retest_result').hide();                                                $('.selected_result').show();                                            } else if (selected_val == "Rejected") {                                                $('.selected_result').hide();                                                $('.retest_result').hide();                                                $('.rejected_result').show();                                            } else if (selected_val == "Retest") {                                                $('.selected_result').hide();                                                $('.rejected_result').hide();                                                $('.retest_result').show();                                            }                                        });                                        $('#experience_add_btn').click(function () {                                            var experience_add = "experience_add";                                            experience_increment = $('#experience_increment').val();                                            added_exp = +experience_increment + 1;                                            $('.experience_div').append(                                                    '<div class="row">'                                                    + '<div class="form-group col-md-4">'                                                    + '<label for="fax">Company: </label>'                                                    + '<input type="text"  name="experience[' + experience_increment + '][experience_company]"  class="form-control" > '                                                    + '</div>'                                                    + '<div class="form-group col-md-4">'                                                    + '<label for="phone">Designation: </label>'                                                    + '<select class="form-control mb-10" name="experience[' + experience_increment + '][experience_designation]"  >'                                                    + '<option selected="" value=""> Select</option>'                                                    + '<option value="Married">Sample</option>'                                                    + '<option value="Not Married">Sample</option>'                                                    + '</select>'                                                    + '</div>'                                                    + '</div>'                                                    + '<div class="row">'                                                    + '<div class="form-group col-md-4">'                                                    + '<label for="scstart">From: </label>'                                                    + '<input type="text" name="experience[' + experience_increment + '][experience_from]" class="form-control date_pick"/>'                                                    + '</div>'                                                    + '<div class="form-group col-md-4 ">'                                                    + '<label for="scstart">To: </label>'                                                    + '<input type="text" name="experience[' + experience_increment + '][experience_to]" class="form-control date_pick"/>'                                                    + '</div>'                                                    + '</div>'                                                    );                                            $('#experience_increment').val(added_exp);                                        });                                        function upload_files(element) {                                            $(element).parent('span').siblings('.file_status_img').html("<img src='assets/images/buffering.gif' width='30' height='30' />");                                            var section = $(element).siblings('.dfile').val();                                            var cp_id = "<?php echo $candidate_code ?>";                                            var numf = 0;                                            var file_ok = 0;                                            var formData = new FormData();                                            $.each($(element)[0].files, function (i, file) {                                                var fileSize = this.size;                                                var sizeLimit = 2000000;                                                if (fileSize > sizeLimit) {                                                    file_ok = 0;                                                    $(element).parent('span').siblings('.file_status_img').hide();                                                    $(element).parent('span').siblings('.file_status_warn').css("color", "red");                                                    $(element).parent('span').siblings('.file_status_warn').html("File size must be less than 2MB");                                                } else {                                                    file_ok = 1;                                                    formData.append('file-' + i, file);                                                    numf = numf + 1;                                                    $(this).closest('input').val();                                                }                                            });                                            if (file_ok == 1) {                                                $.ajax({                                                    url: "candidate_fileup.php?numf=" + numf + '&cp_id=' + cp_id + '&section=' + section,                                                    type: "POST",                                                    cache: false,                                                    contentType: false,                                                    processData: false,                                                    data: formData,                                                    success: function (data) {                                                        $(element).parent('span').siblings('.file_status_img').hide();                                                        $(element).parent('span').siblings('.file_status_warn').hide();                                                        $(element).parent('span').siblings('.file_status').css("color", "#428bca");                                                        $(element).parent('span').siblings('.file_status').append(data);                                                    }                                                });                                            }                                        }    </script></script><script type="text/javascript">    $("#camera").click(function () {        $("#camcontent").show();    });    Webcam.set({        width: 320,        height: 240,        image_format: 'jpeg',        jpeg_quality: 90    });    Webcam.attach('#my_camera');    function preview_snapshot() {        // freeze camera so user can preview pic        Webcam.freeze();        // swap button sets        document.getElementById('pre_take_buttons').style.display = 'none';        document.getElementById('post_take_buttons').style.display = '';    }    function cancel_preview() {        // cancel preview freeze and return to live camera feed        Webcam.unfreeze();        // swap buttons back        document.getElementById('pre_take_buttons').style.display = '';        document.getElementById('post_take_buttons').style.display = 'none';    }    function save_photo() {        // actually snap photo (from preview freeze) and display it        Webcam.snap(function (data_uri) {            // display results in page            document.getElementById('results').innerHTML =                    '<h2>Here is your image:</h2>' +                    '<img src="' + data_uri + '"/>';            // swap buttons back            document.getElementById('pre_take_buttons').style.display = '';            document.getElementById('post_take_buttons').style.display = 'none';            Webcam.upload(data_uri, 'upload.php', function (code, text) {                // Upload complete!                // 'code' will be the HTTP response code from the server, e.g. 200                // 'text' will be the raw response content            });        });        Webcam.reset();    }</script></body></html>