<?phpinclude('connection.php');if (isset($_POST['skill'])) {    $value_array = array();    $skill = $_POST["skill"];$result=array();	     foreach ($skill as $key => $value_array) {		$skill_name = $value_array['skill_name'];		$skill_job = $value_array['skill_job'];        $skill_category = $value_array['skill_category'];        $check_id =$db->selectQuery("select * from sm_job_skill where skill_name='$skill_name' and skill_category='$skill_category' and skill_job='$skill_job'and skill_status='1'");				if(count($check_id)>0)		{   						array_push($result,"0");											}		else 		{         $db->secure_insert("sm_job_skill", $value_array);							array_push($result, "1");        unset($value_array);		}				}		echo json_encode($result);     }?>		